import Link from 'next/link';
import Navbar from '../components/Navbar';
import Head from 'next/head'
import {fetchWeather} from './api/fetchWeather';
import budgetStyle from '../styles/budgeting.module.scss'
import React, { useState } from 'react';


function Budgeting() {

	const [query, setQuery] = useState('');
		const [weather, setWeather] = useState({});
		
		const search = async (e) => {
			if(e.key === 'Enter') {
				const data = await fetchWeather(query);
	
				setWeather(data);
				setQuery('');
			}
		}

	return (
		<>
			<Head>
			  <title>Budgeting</title>
			</Head>

			<div style = {{ backgroundImage:'url("https://www.heritagepark.ca/sites/default/files/2019-10/cia-1500x525_.jpg")', backgroundSize: 'cover'}}>
				<Navbar />

				<section className="hero">
					<div className="container">
						<div className="text-wrapper w-full">
						<h1 className="title" style ={{color: 'white'}}>
							<br></br>
							<br></br>
							<br></br>Budgets<br></br><br></br></h1>
						<p className="description" style ={{color: 'white'}}>
						<br></br><br></br><br></br><br></br><br></br><br></br></p>
						</div>
					</div>
				</section>
			</div>
			

			<div className={budgetStyle.main}>
            <input type="text"className={budgetStyle.search}placeholder="Search..."value={query}onChange={(e) => setQuery(e.target.value)}onKeyPress={search}/>
            {weather.main && (
                <div className={budgetStyle.city}>
                    <h2 className={budgetStyle.city-name}>
                        <span>{weather.name}</span>
                        <sup>{weather.sys.country}</sup>
                    </h2>
                    <div className={budgetStyle.city-temp}>
                        {Math.round(weather.main.temp)}
                        <sup>&deg;C</sup>
                    </div>
                    <div className={budgetStyle.info}>
                        <img className={budgetStyle.city-icon} src={`https://openweathermap.org/img/wn/${weather.weather[0].icon}@2x.png`} alt={weather.weather[0].description} />
                        <p>{weather.weather[0].description}</p>
                    </div>
                </div>
            )}
        </div>
		</>


		

	);
}

export default Budgeting;
